import React, { Component } from 'react';
import { connect } from 'react-redux';
import { addTodo } from '../actions/actionCreator';
import { bindActionCreators } from 'redux';

class CreateTodo extends Component {
  constructor(props) {
    super(props);
    this.state = {
      todotext: '',
    };
    this.onChangeTodoText = this.onChangeTodoText.bind(this);
  }

  onChangeTodoText(e) {
    this.setState({
      todotext: e.target.value,
    });
  }

  render() {
    return (
      <div>
        <h2 style={{textAlign:'center',paddingBottom:'20px'}}>Todo App </h2>

      <div className="col--10 offset-lg-1 col-md-10 col-sm-12 col-xs-12">
        <input
          onChange={this.onChangeTodoText}
          value={this.state.todotext}
          type="text"
          className="form-control"
          id="input"
          placeholder="add todo here"
        />
        <button
          type="button"
          onClick={() => this.setState({ todotext: '' })}
          style={{ marginTop: '25px', marginRight: '15px' }}
          className="btn btn-danger"
        >
          Cancel
        </button>
        <button
          type="button"
          onClick={() => {
            this.props.addTodo(this.state.todotext);
            this.setState({ todotext: '' });
          }}
          style={{ marginTop: '25px' }}
          className="btn btn-success"
        >
          Add Todo
        </button>
      </div>
      </div>
    );
  }
}

const mapDispatchToProps = (dispatch) => {
  return bindActionCreators(
    {
      addTodo,
    },
    dispatch
  );
};

export default connect(null, mapDispatchToProps)(CreateTodo);
